import { Modal, Row, Col, Spin, Button, Image, Avatar, message, Carousel} from 'antd';
import React, { Component } from 'react';
import ViewAdminProfileModal from '../mypost/ViewAdminProfileModal';
import { GetProfile } from '../../../Controller/Project/memprofile';

class ViewEventDetailsModal extends Component{
  constructor(){
    super();
    this.state = {
      viewProfileData2:"",
      viewLoading2: false,
      viewModal2: false,
      viewProfileStatus2:"",

    }
  }
  
  getAdminProfile = async() => {
    const data = {
        admin_id: this.props.viewData.provider_id,
    }
    try{
        const ret = await GetProfile(data);
        //console.log(ret.data)
        if(ret.data.statusCode === 200){
        let txt = "";
        switch(ret.data.data.status){
            case "A":
                txt = 'Active'
            break;
            case "I":
                txt = 'Inactive'
            break;
            default:
                txt = 'Pending Verify By Administration'
            break;
            }
        this.setState({
            viewProfileData2: ret.data.data,
            viewProfileStatus2: txt,
            viewLoading2: false,
        },()=>{
            //console.log(ret)
        })
    }            
    else{
        message.error(ret.data.msg);
    }       
    //console.log(ret)
    }catch(e){
    message.error("Get Details error");
    console.log(e);
    }
    };

  viewAdminProfileModal= () => {
    this.setState({
      viewLoading2: false,
      viewModal2: true,
    },()=>{
      this.getAdminProfile();
    })
  };

  
  onCloseViewAdminProfileModal= () => {
    this.setState({
      viewLoading2: false,
      viewModal2: false,
      viewProfileData2: "",
      viewProfileStatus2: "",
    },()=>{
      //this.getData();
    })
  };


  render() {
    const {
      viewData,
      loadingModal,
      viewPhoto,
      viewStatusDescription
    } = this.props;

    

    return (
      <>
        <Modal
          visible={this.props.visible}
          maskClosable={false}
          closable={false}
          title="Neighborhood Event Details"
          footer={null}
        >
          <Spin spinning={loadingModal}>
          <Row align="middle">
            <Col span={24}>
              <Carousel autoplay>
              {(() => {
                  if(viewPhoto !== ""){
                  var pic = viewPhoto.split(",");
                  return(
                  pic.map((a) => 
                      <div className='carousel_content'>
                        <Image
                          width={400}
                          height={235}
                          src={a}
                        />
                      </div>

                  )
                  )

                }else{
                  return(
                    <div className='carousel_content'>
                        <Image
                          width={400}
                          height={235}
                          src="data:image/png;base64,"
                        />
                      </div>
                  )
                }
              })()}
              </Carousel>
            </Col>
          </Row>
          <br />
          <Row align="middle">
            <Col span={24}>
              <Avatar src={<Image src={viewData.provider_avatar} style={{ width: 32, height: 32 }}
                fallback="data:image/png;base64,"
              />} />&nbsp;&nbsp;
              <b className='p_size_large' onClick={()=>this.viewAdminProfileModal()}>{viewData.provider_email} (Admin)</b>
            </Col>
          </Row>
          <br />
          <br />
          <Row align="middle">
            <Col span={8}>
                <span><b>Event Title</b></span>
            </Col>
            <Col span={16}>
                <span>{viewData.title}</span>
            </Col>
          </Row>
          <br />
          <Row align="middle">
            <Col span={8}>
                <span><b>Code</b></span>
            </Col>
            <Col span={16}>
                <span>{viewData.code}</span>
            </Col>
          </Row>
          <br />
          <Row align="middle">
            <Col span={8}>
                <span><b>Category</b></span>
            </Col>
            <Col span={16}>
                <span>{viewData.category}</span>
            </Col>
          </Row>
          <br />
          <Row align="middle">
            <Col span={8}>
                <span><b>Description</b></span>
            </Col>
            <Col span={16}>
                <span>{viewData.description}</span>
            </Col>
          </Row>
          <br />
          <Row align="middle">
            <Col span={8}>
                <span><b>Date</b></span>
            </Col>
            <Col span={16}>
                <span>{viewData.date}</span>
            </Col>
          </Row>
          <br/>
          <Row align="middle">
            <Col span={8}>
                <span><b>Venue</b></span>
            </Col>
            <Col span={16}>
                <span>{viewData.venue}</span>
            </Col>
          </Row>
          <br/>
          <Row align="middle">
            <Col span={8}>
                <span><b>Price</b></span>
            </Col>
            <Col span={16}>
                <span>RM {viewData.price}</span>
            </Col>
          </Row>
          <br />
          <Row align="middle">
            <Col span={8}>
                <span><b>Payment Method</b></span>
            </Col>
            <Col span={16}>
                <span>{viewData.payment_method}</span>
            </Col>
          </Row>
          <br />
          <Row align="middle">
            <Col span={12}>
                <span><b>Total Amount For Request</b></span>
            </Col>
            <Col span={12}>
                <span>{viewData.amount}</span>
            </Col>
          </Row>
          <br />
          <Row align="middle">
            <Col span={12}>
                <span><b>Balance Amount For Request</b></span>
            </Col>
            <Col span={12}>
                <span>{viewData.balance}</span>
            </Col>
          </Row>
          <br />
          <Row align="middle">
            <Col span={8}>
                <span><b>Status</b></span>
            </Col>
            <Col span={16}>
                <span>{viewStatusDescription}</span>
            </Col>
          </Row>
          <br />
          <Row align="middle">
            <Col span={8}>
                <span><b>Status At</b></span>
            </Col>
            <Col span={16}>
                <span>{viewData.status_at}</span>
            </Col>
          </Row>
          <br />

          <Row>
            <Col span={24} className="right-item">
              <Button type="primary" onClick={()=>this.props.proceed()} style={{visibility: viewData.status==="P" ? "visible" : "hidden"}}>Request Service</Button>
              <Button onClick={()=>this.props.onClose()}>Close</Button>&nbsp;&nbsp;&nbsp;
            </Col>
          </Row>
          </Spin>
        </Modal>

        <>
        <ViewAdminProfileModal
          visible={this.state.viewModal2}
          onClose={this.onCloseViewAdminProfileModal}
          loadingModal={this.state.viewLoading2}
          viewData={this.state.viewProfileData2}
          viewStatus={this.state.viewProfileStatus2}
        />
        </>
      </>
      
      
    )
  }
}

export default  ViewEventDetailsModal;